static int netif_alloc_rx_queues(struct net_device *dev)
{
    unsigned int i, count = dev->num_rx_queues;
    struct netdev_rx_queue *rx;
    size_t sz = count * sizeof(*rx);
    int err = 0;

    BUG_ON(count _rx = rx;
    ...
}

int register_netdevice(struct net_device *dev)
{
    ...
    dev_init_scheduler(dev);
    ...
}

void dev_init_scheduler(struct net_device *dev)
{
    // 设置网络设备排队规则
    dev->qdisc = &noop_qdisc;
    netdev_for_each_tx_queue(dev, dev_init_scheduler_queue, &noop_qdisc);
    if (dev_ingress_queue(dev))
        dev_init_scheduler_queue(dev, dev_ingress_queue(dev), &noop_qdisc);

    timer_setup(&dev->watchdog_timer, dev_watchdog, 0);
}

static void dev_init_scheduler_queue(struct net_device *dev,
                     struct netdev_queue *dev_queue,
                     void *_qdisc)
{
    struct Qdisc *qdisc = _qdisc;

    // 排队规则设置到netdev_queue中的qdisc指针中
    rcu_assign_pointer(dev_queue->qdisc, qdisc);
    dev_queue->qdisc_sleeping = qdisc;
}

排队规则注册
每种排队规则qdisc作为一个独立的驱动实现。驱动加载和卸载会执行qdisc的注册和注销。
注册和注销实现也相当简单，仅仅对qdisc_base链表增加或删除结点。
/* The list of all installed queueing disciplines. */

static struct Qdisc_ops *qdisc_base;

int register_qdisc(struct Qdisc_ops *qops)
{
    struct Qdisc_ops *q, **qp;
    int rc = -EEXIST;

    write_lock(&qdisc_mod_lock);
    for (qp = &qdisc_base; (q = *qp) != NULL; qp = &q->next)
        if (!strcmp(qops->id, q->id))
            goto out;

    ...

    qops->next = NULL;
    *qp = qops;
    rc = 0;
out:
    write_unlock(&qdisc_mod_lock);
    return rc;
}

int unregister_qdisc(struct Qdisc_ops *qops)
{
    struct Qdisc_ops *q, **qp;
    int err = -ENOENT;

    write_lock(&qdisc_mod_lock);
    for (qp = &qdisc_base; (q = *qp) != NULL; qp = &q->next)
        if (q == qops)
            break;
    if (q) {
        *qp = q->next;
        q->next = NULL;
        err = 0;
    }
    write_unlock(&qdisc_mod_lock);
    return err;
}

系统启动默认会注册一部分排队规则：
static int __init pktsched_init(void)
{
    int err;

    err = register_pernet_subsys(&psched_net_ops);
    if (err) {
        pr_err("pktsched_init: "
               "cannot initialize per netns operations\n");
        return err;
    }

    // 注册 pfifo_fast
    register_qdisc(&pfifo_fast_ops);
    // 注册 pfifo
    register_qdisc(&pfifo_qdisc_ops);
    // 注册 bfifo
    register_qdisc(&bfifo_qdisc_ops);
    // 注册 pfifo_head_drop （首部丢弃）
    register_qdisc(&pfifo_head_drop_qdisc_ops);
    // 注册 mq （多队列）
    register_qdisc(&mq_qdisc_ops);
    // 注册 noqueue （多队列）
    register_qdisc(&noqueue_qdisc_ops);

    // rtnetlink
    rtnl_register(PF_UNSPEC, RTM_NEWQDISC, tc_modify_qdisc, NULL, 0);
    rtnl_register(PF_UNSPEC, RTM_DELQDISC, tc_get_qdisc, NULL, 0);
    rtnl_register(PF_UNSPEC, RTM_GETQDISC, tc_get_qdisc, tc_dump_qdisc,
              0);
    rtnl_register(PF_UNSPEC, RTM_NEWTCLASS, tc_ctl_tclass, NULL, 0);
    rtnl_register(PF_UNSPEC, RTM_DELTCLASS, tc_ctl_tclass, NULL, 0);
    rtnl_register(PF_UNSPEC, RTM_GETTCLASS, tc_ctl_tclass, tc_dump_tclass,
              0);

    return 0;
}

subsys_initcall(pktsched_init);

noqueue qdisc队列规则：

没有类
没有调度程序
丢弃所有排队的包
没有速率限制
不对数据包进行分类

noqueue 真正的意思是：不要将此数据包排队。
当数据包通过设备发送，它检查它是否正在使用“noqueue”。如果是，设备立即发送数据包，如果无法发送，则将其丢弃。
发包流程
netfilter走完POST_ROUTING，就是要发包了。我们跟着ip_finish_output进去，最后走到dev_queue_xmit。在这里我们看到了流控的入口。
static int __dev_queue_xmit(struct sk_buff *skb, struct net_device *sb_dev)
{
    struct net_device *dev = skb->dev;
    struct netdev_queue *txq;
    ...
    // 获取网口设备发包队列 netdev_queue
    txq = netdev_core_pick_tx(dev, skb, sb_dev);
    // 获取排队规则 Qdisc
    q = rcu_dereference_bh(txq->qdisc);
    if (q->enqueue) {
        // 如果有入队回调，则执行 __dev_xmit_skb
        rc = __dev_xmit_skb(skb, q, dev, txq);
        goto out;
    }
    ...
}

struct netdev_queue *netdev_core_pick_tx(struct net_device *dev,
                     struct sk_buff *skb,
                     struct net_device *sb_dev)
{
    int queue_index = 0;
    ...
    return netdev_get_tx_queue(dev, queue_index);
}

static inline struct netdev_queue *netdev_get_tx_queue(const struct net_device *dev, unsigned int index)
{
    // _tx 在前面已经设置过
    return &dev->_tx[index];
}

static inline int __dev_xmit_skb(struct sk_buff *skb, struct Qdisc *q,
                 struct net_device *dev,
                 struct netdev_queue *txq)
{
    ...
    // 如果队列不为空
    if (unlikely(!nolock_qdisc_is_empty(q))) {
        // 将数据包入队
        rc = dev_qdisc_enqueue(skb, q, &to_free, txq);
        // 执行发包流程（出队）
        __qdisc_run(q);
        qdisc_run_end(q);
        goto no_lock_out;
    }
    ...
    // 直接发包
    if (sch_direct_xmit(skb, q, dev, txq, NULL, true) && !nolock_qdisc_is_empty(q))
        // 继续发送
        __qdisc_run(q);

    qdisc_run_end(q);
    // 返回成功
    return NET_XMIT_SUCCESS;
}

// 后面发包最终会走到 ndo_start_xmit，
// 前面e1000设置了回调 e1000_xmit_frame。
dev_hard_start_xmit -> xmit_one -> netdev_start_xmit -> ndo_start_xmit

八、总结

XDP/.body:
eXpress Data Path
XDP（eXpress Data Path）为Linux内核提供了高性能、可编程的网络数据路径。由于网络包在还未进入网络协议栈之前就处理，它给Linux网络带来了巨大的性能提升。
XDP主要的特性包括：

基于eBPF
在网络协议栈前处理
无锁设计
批量I/O操作
轮询式
直接队列访问
不需要分配skbuff
支持网络卸载
DDIO
XDP程序快速执行并结束，没有循环
Packeting steering

工作模式
XDP支持3种工作模式，默认使用native模式：

Native XDP：在native模式下，XDP BPF程序运行在网络驱动的早期接收路径上（RX队列），因此，使用该模式时需要网卡驱动程序支持。
Offloaded XDP：在Offloaded模式下，XDP BFP程序直接在NIC（Network Interface Controller）中处理数据包，而不使用主机CPU，相比native模式，性能更高。
Generic XDP：Generic模式主要提供给开发人员测试使用，对于网卡或驱动无法支持native或offloaded模式的情况，内核提供了通用的generic模式，运行在协议栈中，不需要对驱动做任何修改。生产环境中建议使用native或offloaded模式。

包处理流程
基于内核的eBPF程序处理包，每个RX队列分配一个CPU，且以每个网络包一个Page（packet-page）的方式避免分配skbuff。



内核源码
只有部分网卡驱动支持了XDP，比如Inter e1000不支持，Broadcom bnxt支持。
找到内核代码路径：drivers/net/ethernet/broadcom/bnxt/
