/* returns the following:
 * true    - packet consumed by XDP and new buffer is allocated.
 * false   - packet should be passed to the stack.
 */
bool bnxt_rx_xdp(struct bnxt *bp, struct bnxt_rx_ring_info *rxr, u16 cons, struct page *page, u8 **data_ptr, unsigned int *len, u8 *event)
{
    struct bpf_prog *xdp_prog = READ_ONCE(rxr->xdp_prog);
    struct xdp_buff xdp;
    u32 act;

    // 执行bpf程序
    act = bpf_prog_run_xdp(xdp_prog, &xdp);

    switch (act) {
    // 将数据包传递到协议栈处理
    case XDP_PASS:
        return false;

    // 转发数据包，将接收到的数据包发送回数据包到达的同一网卡
    case XDP_TX:
        ...
        *event = BNXT_TX_EVENT;
        __bnxt_xmit_xdp(bp, txr, mapping + offset, *len,
                NEXT_RX(rxr->rx_prod));
        bnxt_reuse_rx_data(rxr, cons, page);
        return true;

    // 重定向数据包，将数据包送到另一块网卡或传入到BPF的cpumap中
    case XDP_REDIRECT:
        ...
        if (xdp_do_redirect(bp->dev, &xdp, xdp_prog)) {
            trace_xdp_exception(bp->dev, xdp_prog, act);
            page_pool_recycle_direct(rxr->page_pool, page);
            return true;
        }

        *event |= BNXT_REDIRECT_EVENT;
        break;

    // eBPF程序发生错误，并导致数据包被丢弃
    case XDP_ABORTED:
        trace_xdp_exception(bp->dev, xdp_prog, act);
        fallthrough;

    // 丢弃数据包
    case XDP_DROP:
        bnxt_reuse_rx_data(rxr, cons, page);
        break;
    }
    return true;
}

